package algo3.fiuba.modelo.cartas.estados_cartas;import algo3.fiuba.modelo.excepciones.CartaInhabilitadaParaActivarseExcepcion;import algo3.fiuba.modelo.jugador.Jugador;import algo3.fiuba.modelo.cartas.Carta;import algo3.fiuba.modelo.cartas.Monstruo;import algo3.fiuba.modelo.cartas.NoMonstruo;import algo3.fiuba.modelo.cartas.Trampa;import algo3.fiuba.modelo.cartas.efectos.EfectoCarta;public abstract class FueraDeJuego implements EstadoCarta {    public boolean estaEnJuego() {        return false;    }    @Override    public void activarEfecto(Carta carta, EfectoCarta efecto) {        throw new RuntimeException("No se pueden activar cartas que están fuera de juego.");    }        @Override    public void recibirAtaque(Monstruo carta) {        throw new RuntimeException("No se pueden atacar Monstruos que están fuera de juego.");    }    @Override    public void recibirAtaque(NoMonstruo carta) {        throw new RuntimeException("No se pueden atacar cartas No Monstruos.");    }    @Override    public void activarEfecto(Trampa trampa, Monstruo atacante, Monstruo atacado, EfectoCarta efecto) {        throw new CartaInhabilitadaParaActivarseExcepcion("No se pueden activar cartas trampas que están fuera de juego");    }    @Override    public void activarEfecto(Carta carta, Monstruo otraCarta1, Monstruo otraCarta2, EfectoCarta efecto) {        activarEfecto(carta, efecto);    }    @Override    public void activarEfecto(Carta carta, Jugador jugador, EfectoCarta efecto){        activarEfecto(carta, jugador, efecto);    }    @Override    public EstadoCarta girarCarta() {        return this;    }    @Override    public boolean estaBocaAbajo() {        return false;    }    @Override    public boolean estaEnMazo() {        return false;    }}